template.value:
/**
 * 静态实例。
 *
 * @var $CLASS$
 */
static private $instance = NULL;

/**
 * 静态创建 $CLASS$ (Singleton) 实例。
 *
 * @param AbstractBootstrap $bootstrap 指定 AbstractBootstrap 实例引用。
 * @return $CLASS$
 */
static function instance(AbstractBootstrap $bootstrap) {
    if (!self::$instance)
        self::$instance = new self($bootstrap);

    return self::$instance;
}
